import nengomodel= nengo.Network()with model:    food = nengo.Node([0,0])    food_rep = nengo.Ensemble(200, 2)    food_rep.radius = 1.4        nengo.Connection(food, food_rep)        position = nengo.Ensemble(1000,2, radius=1.4)        nengo.Connection(position, position, synapse=0.1)        motor = nengo.Ensemble(300, 2)    nengo.Connection(food_rep, motor)        nengo.Connection(motor, position, transform=0.1)        nengo.Connection(position, motor, transform=-1)        home = nengo.Node(0)    home_repn = nengo.Ensemble(300, 3)        def go_home(x):        home, pos_x, pos_y = x        if home > 0:            return -pos_x, -pos_y        else:            return 0,0        nengo.Connection(home, home_repn[0])    nengo.Connection(position, home_repn[1:])        nengo.Connection(home_repn, motor, function=go_home)            