import nengoimport nengo.spa as spaD = 32  # the dimensionality of the vectorsmodel = spa.SPA()with model:    food = nengo.Node([0,0])    model.food_rep = spa.State(2)    nengo.Connection(food, model.food_rep.input)        position = nengo.Ensemble(1000,2)    model.motor = spa.State(2)        #nengo.Connection(model.food_rep.output, model.motor.input)    nengo.Connection(model.motor.output, position, transform=.1, synapse=.1)    nengo.Connection(position, position, synapse=.1)        nengo.Connection(position, model.motor.input, transform=[[-1,0],[0,-1]])        model.task = spa.State(D)    model.home = spa.State(2)    nengo.Connection(position, model.home.input, transform=[[-1,0],[0,-1]])        actions = spa.Actions(        'dot(task, MOVE) --> motor=food_rep',        'dot(task, HOME) --> motor=home',        )    model.bg = spa.BasalGanglia(actions)    model.thalamus = spa.Thalamus(model.bg)